if not doc.isDocument(): return []
tipo_app=doc.getItem('iol_tipo_app')
relazione_asseverata="Relazione asseverata|rel_asseverata_%s" %tipo_app autocertificazione_sicurezza="Autocertificazione sicurezza|autocertificazione_sicurezza" autocertificazione_imprese="Autocertificazione impresa esecutrice %s|autocertificazione_impresa"
#dipende dal tipo nel campo relazione_paesaggistica relazione_paesaggistica = { "1":"Relazione Paesaggistica Semplificata|rel_paes_semplificata", "2":"Relazione Paesaggistica Completa|rel_paes_completa" }
elenco=[] elenco.append(comunicazione[tipo_app]+"&pdf=1")
#if tipo_app=='art22': # elenco.append("Relazione descrittiva per opere interne alle costruzioni|rel_descrittiva_art22")
if tipo_app in ['scia','scia2','pdc']: elenco.append(relazione_asseverata+"&pdf=1")
if doc.getItem('resp_sicurezza_nome'): elenco.append(autocertificazione_sicurezza+"&pdf=1")
if context.config().get('autocertificazione_imprese'): if doc.getItem('lavori_economia_opt','') =='2': elenco.append(autocertificazione_imprese %1) esecutori = doc.getItem('altri_esecutori_grid', [['prova 2'],['prova 3'],['prova 4']]) s=autocertificazione_imprese for i,v in enumerate(esecutori): autocertificazione_imprese = s + "&grid=altri_esecutori_grid&grid_index=" + str(i) elenco.append(autocertificazione_imprese %(i+2))
if tipo_app=='paesaggistica-ordinaria': opt = doc.getItem('relazione_paesaggistica') if opt=='1': elenco.append("Relazione Paesaggistica Completa|rel_paes_completa") elif opt=='2': elenco.append("Relazione Paesaggistica Semplificata|rel_paes_semplificata")
if tipo_app=='paesaggistica-semplificata': elenco.append("Relazione Paesaggistica Semplificata|rel_paes_semplificata")
if tipo_app=='paesaggistica-accertamento': elenco.append("Relazione Paesaggistica Completa|rel_paes_completa")
if tipo_app=='cila': elenco.append("Dichiarazioni del Progettista|rel_dichiarazioni_progettista_cila&pdf=1")
if tipo_app=='finelavori': elenco.append("Dichiarazioni del Progettista|rel_dichiarazioni_progettista_fine_lavori&pdf=1")
elenco_tipo_relazione = context.config().get('relazione_descrittiva',['art22'])
if tipo_app in elenco_tipo_relazione: elenco.append("Relazione descrittiva per opere interne alle costruzioni|rel_descrittiva_%s" %tipo_app)
#######AGGIUNGO I PARAMETRI
app=context.getParentDatabase().getId().replace('iol_','') for k in range(len(elenco)): if 'rel_' in elenco[k]: elenco[k] = "%s&app=%s&grp=relazioni&form=sub_completa_%s" %(elenco[k],app,tipo_app) else: elenco[k] = "%s&app=%s&grp=autorizzazione&form=sub_completa_%s" %(elenco[k],app,tipo_app)
return elenco
