## Script (Python) "getPianificazione" ##bind container=container ##bind context=context ##bind namespace= ##bind script=script ##bind subpath=traverse_subpath ##parameters= ##title= ## # -*- coding: utf-8 -*- from Products.CMFPlomino.PlominoUtils import json_dumps ret = [] sezioni = context.REQUEST.get('sezioni','').split(',') fogli = context.REQUEST.get('fogli','').split(',') mappali = context.REQUEST.get('mappali','').split(',') comune = context.REQUEST.get('comune','')
orFilters = [] for index in range(len(mappali)): andFilters = [] andFilters.append("comune='%s'" %comune) andFilters.append("mappale='%s'" %mappali[index]) andFilters.append("foglio='%s'" %fogli[index]) if context.REQUEST.get('sezioni'): andFilters.append("sezione='%s'" %sezioni[index]) andFilters = "(" + " AND ".join(andFilters) + ")" orFilters.append(andFilters)
sFilter = " OR ".join(orFilters)
pianificazione = 'Pianificazione_%s' %context.REQUEST.get('pianificazione','')
if pianificazione in context.aq_parent.zsql: for res in context.aq_parent.zsql[pianificazione](filter = sFilter).dictionaries(): ret.append(res)
return json_dumps({"success":1, "filter": sFilter, "results":ret})

